export default {
  name: "Form",
  data: function data() {
    return {
      form: {
        subject: "",
        description: ""
      },
      methodSave: "new"
    };
  },
  created: function created() {
    if (this.$route.params.index === 0 || this.$route.params.index !== undefined) {
      this.methodSave = "update";
      var tasks = JSON.parse(localStorage.getItem("tasks"));
      this.form = tasks[this.$route.params.index];
    }
  },
  methods: {
    saveTask: function saveTask() {
      if (this.methodSave === "update") {
        var _tasks = JSON.parse(localStorage.getItem("tasks"));

        _tasks[this.$route.params.index] = this.form;
        localStorage.setItem("tasks", JSON.stringify(_tasks));
        this.$router.push({
          name: "list"
        });
        return;
      }

      var tasks = localStorage.getItem("tasks") ? JSON.parse(localStorage.getItem("tasks")) : [];
      tasks.push(this.form);
      localStorage.setItem("tasks", JSON.stringify(tasks));
      this.$router.push({
        name: "list"
      });
    }
  }
};
